contracts/auction/AAuction.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

// Internal imports
import "./IAuction.sol";
import "./ISeller.sol";
import "./IBidder.sol";
import "./Donatable.sol";

/// @title An abstract contract for Auctions
/// @author Hoang Duc Bach
/// @notice You can use this contract to create an auction
/// @dev This contract is an abstract contract for Auctions

abstract contract AAuction is IAuction, ISeller, IBidder, Donatable {
    // Auctions
    mapping(address => Auction) public auctions;

    // Bids
    mapping(address => mapping(address => Bid)) public bids;

    // Donors
    mapping(address => uint256) public donors;


    /**
     * @dev Donation is given in addition to the winner, this is a separate amount not included in the highest bid
     */
    uint256 public donation = 0;

    constructor() {}

    /*
    || MODIFIERS
    */

    /**
     * @dev Modifier to check auction is on going
     */
    modifier onlyAuctionOnGoing(address _auctionId) {
        require(
            auctions[_auctionId].startedAt > 0 &&
                auctions[_auctionId].endedAt == 0,
            "Auction is not on going"
        );
        _;
    }
}

contracts/auction/Donatable.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

/// @title An interface Donatable contract
/// @author Hoang Duc Bach
/// @notice You can use this contract to donate to a charity
/// @dev This contract is an interface for Donatable contract

interface Donatable {
    /*
    || EVENTS
    */

    /**
     * @dev Event to log a donation
     * @param donor The address of the donor who donated.
     * @param amount The amount of the donation.
     * @param timestamp The timestamp when the donation was made.
     */
    event Donation(address indexed donor, uint256 amount, uint256 timestamp);

    /*
    || FUNCTIONS
    */


    /**
     * @dev Donate to a charity
     */
    function donate() external payable;
}

